import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar as BootstrapNavbar, Nav, Container, Button, NavDropdown, Form, InputGroup, Image } from 'react-bootstrap';
import { FaHome, FaBox, FaEnvelope, FaUser, FaHeart, FaSignOutAlt, FaSearch } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import config from '../config/config';
import NotificationBell from './NotificationBell';

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const getProfileImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith('http')) return imagePath;
    return `${config.api.backendURL}${imagePath}`;
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/items?search=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  return (
    <BootstrapNavbar bg="dark" variant="dark" expand="lg" sticky="top">
      <Container>
        <BootstrapNavbar.Brand as={Link} to="/" className="fw-bold">
          <FaBox className="me-2" />
          ShareHub
        </BootstrapNavbar.Brand>

        <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />

        <BootstrapNavbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/">
              <FaHome className="me-1" /> Home
            </Nav.Link>

            <Nav.Link as={Link} to="/items">
              <FaBox className="me-1" /> Items
            </Nav.Link>

            {isAuthenticated() && (
              <>
                <Nav.Link as={Link} to="/messages">
                  <FaEnvelope className="me-1" /> Messages
                </Nav.Link>

                <Nav.Link as={Link} to="/favorites">
                  <FaHeart className="me-1" /> Favorites
                </Nav.Link>
              </>
            )}
          </Nav>

          {/* Search Bar */}
          <Form onSubmit={handleSearch} className="d-none d-lg-flex mx-3" style={{ minWidth: '300px' }}>
            <InputGroup size="sm">
              <Form.Control
                type="text"
                placeholder="Search items..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <Button variant="outline-light" type="submit">
                <FaSearch />
              </Button>
            </InputGroup>
          </Form>

          <Nav className="ms-auto align-items-center">
            {isAuthenticated() ? (
              <>
                {/* Notification Bell */}
                <Nav.Item className="d-flex align-items-center">
                  <NotificationBell />
                </Nav.Item>

                <NavDropdown
                  title={
                    <span className="d-flex align-items-center">
                      {user?.profile_image ? (
                        <Image 
                          src={getProfileImageUrl(user.profile_image)}
                          roundedCircle 
                          width={30} 
                          height={30}
                          style={{ objectFit: 'cover', marginRight: '8px' }}
                          alt="Profile"
                        />
                      ) : (
                        <FaUser className="me-1" />
                      )}
                      {user?.name || 'User'}
                    </span>
                  }
                  id="user-dropdown"
                  align="end"
                >
                  <NavDropdown.Item as={Link} to="/profile">
                    My Profile
                  </NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/my-items">
                    My Items
                  </NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/transactions">
                    Transactions
                  </NavDropdown.Item>
                  {user?.role === 'admin' && (
                    <>
                      <NavDropdown.Divider />
                      <NavDropdown.Item as={Link} to="/admin">
                        Admin Dashboard
                      </NavDropdown.Item>
                    </>
                  )}
                  <NavDropdown.Divider />
                  <NavDropdown.Item onClick={handleLogout}>
                    <FaSignOutAlt className="me-1" /> Logout
                  </NavDropdown.Item>
                </NavDropdown>
              </>
            ) : (
              <>
                <Nav.Link as={Link} to="/login">
                  <Button variant="outline-light" size="sm">
                    Login
                  </Button>
                </Nav.Link>
                <Nav.Link as={Link} to="/register">
                  <Button variant="primary" size="sm">
                    Register
                  </Button>
                </Nav.Link>
              </>
            )}
          </Nav>
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
};

export default Navbar;
