import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Button, Card, Form, InputGroup, Alert, Badge } from 'react-bootstrap';
import { FaSearch, FaTshirt, FaBook, FaBoxOpen, FaHandHoldingHeart, FaExchangeAlt, FaDollarSign, FaPlusCircle, FaEnvelope, FaHeart } from 'react-icons/fa';
import { checkBackendHealth } from '../utils/healthCheck';
import { useAuth } from '../context/AuthContext';

const Home = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [backendStatus, setBackendStatus] = useState(null);

  useEffect(() => {
    // Check backend connection on mount
    const verifyBackend = async () => {
      const result = await checkBackendHealth();
      setBackendStatus(result);
      
      if (result.success) {
        console.log('‚úÖ Backend connected:', result.data);
      } else {
        console.error('‚ùå Backend connection failed:', result.error);
      }
    };

    verifyBackend();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/items?search=${searchQuery}`);
    }
  };

  const categories = [
    { name: 'Clothes', icon: <FaTshirt size={40} />, color: '#FF6B6B', path: '/items?category=clothes' },
    { name: 'Books', icon: <FaBook size={40} />, color: '#4ECDC4', path: '/items?category=books' },
    { name: 'Ration', icon: <FaBoxOpen size={40} />, color: '#FFE66D', path: '/items?category=ration' }
  ];

  const features = [
    { icon: <FaDollarSign size={30} />, title: 'Sell Items', description: 'List your items and earn money' },
    { icon: <FaHandHoldingHeart size={30} />, title: 'Donate', description: 'Help those in need by donating' },
    { icon: <FaExchangeAlt size={30} />, title: 'Exchange', description: 'Swap items with others' }
  ];

  return (
    <div>
      {/* Backend Status Alert */}
      {backendStatus && !backendStatus.success && (
        <Alert variant="danger" className="mb-0 text-center">
          <strong>‚ö†Ô∏è Backend Connection Error:</strong> {backendStatus.message}
          <br />
          <small>Make sure the backend server is running on http://localhost:5000</small>
        </Alert>
      )}

      {/* Hero Section - Conditional Rendering */}
      {isAuthenticated() ? (
        // Logged In User View
        <div className="bg-gradient-primary text-white py-5" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
          <Container>
            <Row className="align-items-center">
              <Col lg={8}>
                <h1 className="display-4 fw-bold mb-3">Welcome back, {user?.name}! üëã</h1>
                <p className="lead mb-4">Ready to share, sell, or find something useful today?</p>
                <div className="d-flex gap-3 flex-wrap">
                  <Button as={Link} to="/create-item" variant="light" size="lg">
                    <FaPlusCircle className="me-2" />
                    Create Listing
                  </Button>
                  <Button as={Link} to="/items" variant="outline-light" size="lg">
                    <FaBoxOpen className="me-2" />
                    Browse Items
                  </Button>
                  <Button as={Link} to="/messages" variant="outline-light" size="lg">
                    <FaEnvelope className="me-2" />
                    Messages
                  </Button>
                </div>
              </Col>
              <Col lg={4} className="mt-4 mt-lg-0">
                <Card className="bg-white bg-opacity-10 border-0 text-white">
                  <Card.Body className="p-4">
                    <h5 className="text-white mb-4">Quick Stats</h5>
                    <div className="d-flex justify-content-around">
                      <div className="text-center">
                        <h3 className="text-white mb-1">
                          <Badge bg="light" text="dark">0</Badge>
                        </h3>
                        <small>Items</small>
                      </div>
                      <div className="text-center">
                        <h3 className="text-white mb-1">
                          <Badge bg="light" text="dark">0</Badge>
                        </h3>
                        <small>Messages</small>
                      </div>
                      <div className="text-center">
                        <h3 className="text-white mb-1">
                          <Badge bg="light" text="dark">0</Badge>
                        </h3>
                        <small>Favorites</small>
                      </div>
                    </div>
                    <div className="mt-4">
                      <Button as={Link} to="/dashboard" variant="light" className="w-100">
                        View Dashboard
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Container>
        </div>
      ) : (
        // Not Logged In View
        <div className="bg-primary text-white py-5">
          <Container>
            <Row className="align-items-center">
              <Col lg={6} className="text-center text-lg-start">
                <h1 className="display-3 fw-bold mb-3">Welcome to ShareHub</h1>
                <p className="lead mb-4">
                  Share, Sell, and Donate items in your community. 
                  Join thousands of users making a difference!
                </p>
                <div className="d-flex gap-3 justify-content-center justify-content-lg-start flex-wrap">
                  <Button as={Link} to="/register" variant="light" size="lg">
                    Get Started
                  </Button>
                  <Button as={Link} to="/login" variant="outline-light" size="lg">
                    Login
                  </Button>
                </div>
              </Col>
              <Col lg={6} className="mt-4 mt-lg-0">
                <div className="bg-white p-4 rounded shadow">
                  <h4 className="text-dark mb-3">Find What You Need</h4>
                  <Form onSubmit={handleSearch}>
                    <InputGroup size="lg">
                      <Form.Control
                        type="text"
                        placeholder="Search for items..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                      />
                      <Button variant="primary" type="submit">
                        <FaSearch /> Search
                      </Button>
                    </InputGroup>
                  </Form>
                </div>
              </Col>
            </Row>
          </Container>
        </div>
      )}

      {/* Categories Section */}
      <Container className="py-5">
        <h2 className="text-center mb-4">Browse by Category</h2>
        <Row className="g-4">
          {categories.map((category, index) => (
            <Col key={index} md={4}>
              <Card 
                className="text-center h-100 shadow-sm hover-shadow" 
                style={{ cursor: 'pointer', border: `3px solid ${category.color}` }}
                onClick={() => navigate(category.path)}
              >
                <Card.Body className="py-5">
                  <div style={{ color: category.color }} className="mb-3">
                    {category.icon}
                  </div>
                  <h4>{category.name}</h4>
                  <Button variant="outline-primary" size="sm" className="mt-3">
                    Browse {category.name}
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* Features Section */}
      <div className="bg-light py-5">
        <Container>
          <h2 className="text-center mb-5">How ShareHub Works</h2>
          <Row className="g-4">
            {features.map((feature, index) => (
              <Col key={index} md={4}>
                <Card className="text-center h-100 border-0 shadow-sm">
                  <Card.Body className="py-4">
                    <div className="text-primary mb-3">{feature.icon}</div>
                    <h5>{feature.title}</h5>
                    <p className="text-muted">{feature.description}</p>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </div>

      {/* CTA Section */}
      <Container className="py-5 text-center">
        <h2 className="mb-3">Ready to Get Started?</h2>
        <p className="lead text-muted mb-4">
          Join our community today and start sharing, selling, or donating items!
        </p>
        <Button as={Link} to="/register" variant="primary" size="lg">
          Create Free Account
        </Button>
      </Container>
    </div>
  );
};

export default Home;
