import { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Container, Row, Col, Form, InputGroup, Button, Alert, Spinner, Badge } from 'react-bootstrap';
import { FaSearch, FaTimes } from 'react-icons/fa';
import ItemCard from '../components/ItemCard';
import Pagination from '../components/Pagination';
import FilterSidebar from '../components/FilterSidebar';
import itemService from '../services/itemService';

const ItemsList = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [paginatedItems, setPaginatedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState(searchParams.get('search') || '');
  const [selectedCategory, setSelectedCategory] = useState(searchParams.get('category') || 'all');
  const [selectedType, setSelectedType] = useState(searchParams.get('type') || 'all');
  
  // Pagination state
  const [currentPage, setCurrentPage] = useState(parseInt(searchParams.get('page')) || 1);
  const [itemsPerPage] = useState(12); // 12 items per page (3x4 grid)

  useEffect(() => {
    fetchItems();
  }, []);

  useEffect(() => {
    filterItems();
  }, [items, searchQuery, selectedCategory, selectedType]);

  useEffect(() => {
    paginateItems();
  }, [filteredItems, currentPage]);

  const fetchItems = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await itemService.getAllItems();
      setItems(response.data || []);
    } catch (err) {
      console.error('Error fetching items:', err);
      setError('Failed to load items. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const filterItems = (advancedFilters = null) => {
    let filtered = [...items];

    // Use advanced filters if provided, otherwise use state
    const filters = advancedFilters || {
      categories: selectedCategory !== 'all' ? [selectedCategory] : [],
      listingType: selectedType !== 'all' ? selectedType : '',
      minPrice: '',
      maxPrice: '',
      condition: '',
      sortBy: 'newest'
    };

    // Filter by search query (title and description)
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(item => 
        item.title?.toLowerCase().includes(query) ||
        item.description?.toLowerCase().includes(query)
      );
    }

    // Filter by categories
    if (filters.categories && filters.categories.length > 0) {
      filtered = filtered.filter(item => 
        filters.categories.includes(item.category?.toLowerCase())
      );
    }

    // Filter by listing type
    if (filters.listingType) {
      filtered = filtered.filter(item => 
        item.listing_type?.toLowerCase() === filters.listingType.toLowerCase()
      );
    }

    // Filter by price range
    if (filters.minPrice) {
      filtered = filtered.filter(item => 
        item.price && parseFloat(item.price) >= parseFloat(filters.minPrice)
      );
    }
    if (filters.maxPrice) {
      filtered = filtered.filter(item => 
        item.price && parseFloat(item.price) <= parseFloat(filters.maxPrice)
      );
    }

    // Filter by condition
    if (filters.condition) {
      filtered = filtered.filter(item => 
        item.item_condition?.toLowerCase() === filters.condition.toLowerCase()
      );
    }

    // Sort items
    if (filters.sortBy) {
      switch (filters.sortBy) {
        case 'newest':
          filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
          break;
        case 'oldest':
          filtered.sort((a, b) => new Date(a.created_at) - new Date(b.created_at));
          break;
        case 'price_low':
          filtered.sort((a, b) => (parseFloat(a.price) || 0) - (parseFloat(b.price) || 0));
          break;
        case 'price_high':
          filtered.sort((a, b) => (parseFloat(b.price) || 0) - (parseFloat(a.price) || 0));
          break;
        case 'title_asc':
          filtered.sort((a, b) => a.title.localeCompare(b.title));
          break;
        case 'title_desc':
          filtered.sort((a, b) => b.title.localeCompare(a.title));
          break;
        default:
          break;
      }
    }

    setFilteredItems(filtered);
    setCurrentPage(1); // Reset to first page when filters change
  };

  const handleFilterChange = (filters) => {
    filterItems(filters);
  };

  const paginateItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    setPaginatedItems(filteredItems.slice(startIndex, endIndex));
  };

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchQuery(value);
    updateURLParams({ search: value, page: 1 });
  };

  const handleClearSearch = () => {
    setSearchQuery('');
    updateURLParams({ search: '', page: 1 });
  };

  const handleCategoryChange = (e) => {
    const value = e.target.value;
    setSelectedCategory(value);
    updateURLParams({ category: value, page: 1 });
  };

  const handleTypeChange = (e) => {
    const value = e.target.value;
    setSelectedType(value);
    updateURLParams({ type: value, page: 1 });
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    updateURLParams({ page });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const updateURLParams = (updates) => {
    const params = new URLSearchParams(searchParams);
    
    Object.entries(updates).forEach(([key, value]) => {
      if (value && value !== 'all' && value !== 1) {
        params.set(key, value);
      } else if (key === 'page' && value === 1) {
        params.delete(key); // Don't show page=1 in URL
      } else {
        params.delete(key);
      }
    });

    setSearchParams(params);
  };

  const handleClearFilters = () => {
    setSearchQuery('');
    setSelectedCategory('all');
    setSelectedType('all');
    setCurrentPage(1);
    setSearchParams({});
  };

  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);

  if (loading) {
    return (
      <Container className="text-center py-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Loading items...</p>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      {/* Page Header */}
      <Row className="mb-4">
        <Col>
          <h2>Browse Items</h2>
          <p className="text-muted">
            {filteredItems.length} {filteredItems.length === 1 ? 'item' : 'items'} found
          </p>
        </Col>
      </Row>

      {/* Search Bar */}
      <Row className="mb-4">
        <Col>
          <Form.Group>
            <InputGroup size="lg">
              <InputGroup.Text>
                <FaSearch />
              </InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="Search by title or description..."
                value={searchQuery}
                onChange={handleSearchChange}
              />
              {searchQuery && (
                <Button 
                  variant="outline-secondary" 
                  onClick={handleClearSearch}
                  title="Clear search"
                >
                  <FaTimes />
                </Button>
              )}
            </InputGroup>
          </Form.Group>
        </Col>
      </Row>

      {/* Error Message */}
      {error && (
        <Alert variant="danger" dismissible onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      <Row>
        {/* Filter Sidebar */}
        <Col lg={3} className="mb-4">
          <FilterSidebar 
            onFilterChange={handleFilterChange}
            initialFilters={{
              categories: selectedCategory !== 'all' ? [selectedCategory] : [],
              listingType: selectedType !== 'all' ? selectedType : ''
            }}
          />
        </Col>

        {/* Items Grid */}
        <Col lg={9}>
          {filteredItems.length > 0 ? (
        <>
          <Row className="g-4">
            {paginatedItems.map(item => (
              <Col key={item.id} xs={12} sm={6} md={4} lg={3}>
                <ItemCard item={item} />
              </Col>
            ))}
          </Row>

          {/* Pagination */}
          <Pagination 
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={handlePageChange}
            maxPagesToShow={5}
          />

          {/* Results Info */}
          <div className="text-center text-muted mt-3">
            <small>
              Showing {((currentPage - 1) * itemsPerPage) + 1} - {Math.min(currentPage * itemsPerPage, filteredItems.length)} of {filteredItems.length} items
            </small>
          </div>
          </>
        ) : (
          <Alert variant="info" className="text-center py-5">
            <h4>No items found</h4>
            <p className="mb-3">
              {searchQuery 
                ? `No items match your search "${searchQuery}"`
                : 'No items available at the moment'
              }
            </p>
            {(searchQuery || selectedCategory !== 'all' || selectedType !== 'all') && (
              <Button variant="primary" onClick={handleClearFilters}>
                Clear Filters
              </Button>
            )}
          </Alert>
        )}
        </Col>
      </Row>
    </Container>
  );
};

export default ItemsList;
