import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Button, Card, Form, InputGroup, Alert, Badge } from 'react-bootstrap';
import { FaSearch, FaTshirt, FaBook, FaBoxOpen, FaHandHoldingHeart, FaExchangeAlt, FaDollarSign, FaPlusCircle, FaEnvelope, FaHeart } from 'react-icons/fa';
import { checkBackendHealth } from '../utils/healthCheck';
import { useAuth } from '../context/AuthContext';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [backendStatus, setBackendStatus] = useState(null);

  useEffect(() => {
    // Check backend connection on mount
    const verifyBackend = async () => {
      const result = await checkBackendHealth();
      setBackendStatus(result);
      
      if (result.success) {
        console.log('‚úÖ Backend connected:', result.data);
      } else {
        console.error('‚ùå Backend connection failed:', result.error);
      }
    };

    verifyBackend();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/items?search=${searchQuery}`);
    }
  };

  const categories = [
    { name: 'Clothes', icon: <FaTshirt size={40} />, color: '#FF6B6B', path: '/items?category=clothes' },
    { name: 'Books', icon: <FaBook size={40} />, color: '#4ECDC4', path: '/items?category=books' },
    { name: 'Ration', icon: <FaBoxOpen size={40} />, color: '#FFE66D', path: '/items?category=ration' }
  ];

  const features = [
    { icon: <FaDollarSign size={30} />, title: 'Sell Items', description: 'List your items and earn money' },
    { icon: <FaHandHoldingHeart size={30} />, title: 'Donate', description: 'Help those in need by donating' },
    { icon: <FaExchangeAlt size={30} />, title: 'Exchange', description: 'Swap items with others' }
  ];

  return (
    <div>
      {/* Backend Status Alert */}
      {backendStatus && !backendStatus.success && (
        <Alert variant="danger" className="mb-0 text-center">
          <strong>‚ö†Ô∏è Backend Connection Error:</strong> {backendStatus.message}
          <br />
          <small>Make sure the backend server is running on http://localhost:5000</small>
        </Alert>
      )}

      {/* Hero Section - Conditional Rendering */}
      {user ? (
        // Logged In User View
        <section className="hero-section bg-gradient-primary text-white py-5">
          <Container>
            <Row className="align-items-center">
              <Col lg={8}>
                <h1 className="display-4 fw-bold mb-3">Welcome back, {user.name}! üëã</h1>
                <p className="lead mb-4">Ready to share, sell, or find something useful today?</p>
                <div className="d-flex gap-3">
                  <Button as={Link} to="/create-item" variant="light" size="lg">
                    <i className="bi bi-plus-circle me-2"></i>
                    Create Listing
                  </Button>
                  <Button as={Link} to="/items" variant="outline-light" size="lg">
                    <i className="bi bi-grid me-2"></i>
                    Browse Items
                  </Button>
                </div>
              </Col>
              <Col lg={4} className="text-center">
                <div className="stats-card bg-white bg-opacity-10 rounded p-4">
                  <h3 className="text-white">Your Stats</h3>
                  <div className="d-flex justify-content-around mt-3">
                    <div>
                      <h4 className="text-white">12</h4>
                      <small>Items</small>
                    </div>
                    <div>
                      <h4 className="text-white">5</h4>
                      <small>Messages</small>
                    </div>
                    <div>
                      <h4 className="text-white">3</h4>
                      <small>Favorites</small>
                    </div>
                  </div>
                </div>
              </Col>
            </Row>
          </Container>
        </section>
      ) : (
        // Not Logged In View
        <section className="hero-section bg-gradient-primary text-white py-5">
          <Container>
            <Row className="align-items-center">
              <Col lg={6}>
                <h1 className="display-3 fw-bold mb-3">Welcome to ShareHub</h1>
                <p className="lead mb-4">
                  Share, Sell, and Donate items in your community. Join thousands of users making a difference!
                </p>
                <div className="d-flex gap-3">
                  <Button as={Link} to="/register" variant="light" size="lg">
                    Get Started
                  </Button>
                  <Button as={Link} to="/login" variant="outline-light" size="lg">
                    Login
                  </Button>
                </div>
              </Col>
              <Col lg={6}>
                <img src="/hero-illustration.svg" alt="ShareHub" className="img-fluid" />
              </Col>
            </Row>
          </Container>
        </section>
      )}

      {/* Categories Section */}
      <section className="categories-section py-5 bg-light">
        <Container>
          <div className="text-center mb-5">
            <h2 className="display-5 fw-bold mb-3">Browse by Category</h2>
            <p className="text-muted">Find exactly what you're looking for</p>
          </div>
          <Row className="g-4">
            {/* Clothes Category */}
            <Col md={4}>
              <div className="category-card bg-white shadow-sm border-3 border-danger border-start">
                <div className="category-icon mb-3">
                  <i className="bi bi-bag fs-1 text-danger"></i>
                </div>
                <h3 className="h4 mb-3">Clothes</h3>
                <p className="text-muted mb-4">Share fashion, donate to those in need, or find great deals</p>
                <Link to="/items?category=clothes" className="btn btn-outline-danger">
                  Browse Clothes
                  <i className="bi bi-arrow-right ms-2"></i>
                </Link>
              </div>
            </Col>

            {/* Books Category */}
            <Col md={4}>
              <div className="category-card bg-white shadow-sm border-3 border-info border-start">
                <div className="category-icon mb-3">
                  <i className="bi bi-book fs-1 text-info"></i>
                </div>
                <h3 className="h4 mb-3">Books</h3>
                <p className="text-muted mb-4">Share knowledge, find rare editions, or donate to libraries</p>
                <Link to="/items?category=books" className="btn btn-outline-info">
                  Browse Books
                  <i className="bi bi-arrow-right ms-2"></i>
                </Link>
              </div>
            </Col>

            {/* Ration Category */}
            <Col md={4}>
              <div className="category-card bg-white shadow-sm border-3 border-warning border-start">
                <div className="category-icon mb-3">
                  <i className="bi bi-basket fs-1 text-warning"></i>
                </div>
                <h3 className="h4 mb-3">Ration</h3>
                <p className="text-muted mb-4">Help feed families, donate essentials, support community</p>
                <Link to="/items?category=ration" className="btn btn-outline-warning">
                  Browse Ration
                  <i className="bi bi-arrow-right ms-2"></i>
                </Link>
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Features Section */}
      <div className="bg-light py-5">
        <Container>
          <h2 className="text-center mb-5">How ShareHub Works</h2>
          <Row className="g-4">
            {features.map((feature, index) => (
              <Col key={index} md={4}>
                <Card className="text-center h-100 border-0 shadow-sm">
                  <Card.Body className="py-4">
                    <div className="text-primary mb-3">{feature.icon}</div>
                    <h5>{feature.title}</h5>
                    <p className="text-muted">{feature.description}</p>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </div>

      {/* CTA Section - Only for non-logged-in users */}
      {!isAuthenticated() && (
        <Container className="py-5 text-center">
          <h2 className="mb-3">Ready to Get Started?</h2>
          <p className="lead text-muted mb-4">
            Join our community today and start sharing, selling, or donating items!
          </p>
          <Button as={Link} to="/register" variant="primary" size="lg">
            Create Free Account
          </Button>
        </Container>
      )}
    </div>
  );
};

export default Home;
