import { useState, useEffect, useRef } from 'react';
import { Card, Form, Button, InputGroup, Alert, Spinner } from 'react-bootstrap';
import { FaMapMarkerAlt, FaSearch, FaCrosshairs } from 'react-icons/fa';
import config from '../config/config';

const LocationPicker = ({ 
  onLocationSelect,
  initialLat = config.maps.defaultCenter.lat,
  initialLng = config.maps.defaultCenter.lng,
  height = '400px',
  showSearch = true,
  showCurrentLocation = true
}) => {
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);
  const [searchBox, setSearchBox] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState({
    lat: initialLat,
    lng: initialLng,
    address: ''
  });
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const mapRef = useRef(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    if (!window.google) {
      setError('Google Maps API not loaded. Please check your API key configuration.');
      return;
    }

    initializeMap();
  }, []);

  const initializeMap = () => {
    try {
      const center = {
        lat: parseFloat(initialLat),
        lng: parseFloat(initialLng)
      };

      const mapInstance = new window.google.maps.Map(mapRef.current, {
        center: center,
        zoom: config.maps.defaultZoom,
        mapTypeControl: true,
        streetViewControl: false,
        fullscreenControl: true,
        zoomControl: true
      });

      setMap(mapInstance);

      // Create marker
      const markerInstance = new window.google.maps.Marker({
        position: center,
        map: mapInstance,
        draggable: true,
        animation: window.google.maps.Animation.DROP,
        title: 'Selected Location'
      });

      setMarker(markerInstance);

      // Get initial address
      getAddressFromLatLng(center.lat, center.lng);

      // Add click listener to map
      mapInstance.addListener('click', (e) => {
        const lat = e.latLng.lat();
        const lng = e.latLng.lng();
        updateLocation(lat, lng);
      });

      // Add drag listener to marker
      markerInstance.addListener('dragend', (e) => {
        const lat = e.latLng.lat();
        const lng = e.latLng.lng();
        updateLocation(lat, lng);
      });

      // Initialize search box if enabled
      if (showSearch && searchInputRef.current) {
        initializeSearchBox(mapInstance);
      }
    } catch (err) {
      setError('Error initializing map: ' + err.message);
      console.error('Map initialization error:', err);
    }
  };

  const initializeSearchBox = (mapInstance) => {
    try {
      const searchBoxInstance = new window.google.maps.places.SearchBox(
        searchInputRef.current
      );

      setSearchBox(searchBoxInstance);

      // Bias search results to map viewport
      mapInstance.addListener('bounds_changed', () => {
        searchBoxInstance.setBounds(mapInstance.getBounds());
      });

      // Listen for place selection
      searchBoxInstance.addListener('places_changed', () => {
        const places = searchBoxInstance.getPlaces();

        if (places.length === 0) {
          return;
        }

        const place = places[0];

        if (!place.geometry || !place.geometry.location) {
          setError('No location data available for this place');
          return;
        }

        const lat = place.geometry.location.lat();
        const lng = place.geometry.location.lng();
        const address = place.formatted_address || '';

        // Update map and marker
        mapInstance.setCenter(place.geometry.location);
        mapInstance.setZoom(15);
        marker.setPosition(place.geometry.location);

        // Update location state
        setSelectedLocation({ lat, lng, address });
        setSearchQuery(address);

        // Notify parent
        if (onLocationSelect) {
          onLocationSelect({ lat, lng, address });
        }
      });
    } catch (err) {
      console.error('Search box initialization error:', err);
    }
  };

  const updateLocation = (lat, lng) => {
    // Update marker position
    if (marker) {
      marker.setPosition({ lat, lng });
    }

    // Center map on new location
    if (map) {
      map.panTo({ lat, lng });
    }

    // Get address for coordinates
    getAddressFromLatLng(lat, lng);
  };

  const getAddressFromLatLng = async (lat, lng) => {
    try {
      const geocoder = new window.google.maps.Geocoder();
      
      geocoder.geocode({ location: { lat, lng } }, (results, status) => {
        if (status === 'OK' && results[0]) {
          const address = results[0].formatted_address;
          
          setSelectedLocation({ lat, lng, address });
          setSearchQuery(address);

          // Notify parent component
          if (onLocationSelect) {
            onLocationSelect({ lat, lng, address });
          }
        } else {
          setSelectedLocation({ lat, lng, address: `${lat.toFixed(6)}, ${lng.toFixed(6)}` });
          
          if (onLocationSelect) {
            onLocationSelect({ lat, lng, address: '' });
          }
        }
      });
    } catch (err) {
      console.error('Geocoding error:', err);
      setSelectedLocation({ lat, lng, address: '' });
    }
  };

  const getCurrentLocation = () => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser');
      return;
    }

    setLoading(true);
    setError('');

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude;
        const lng = position.coords.longitude;
        
        updateLocation(lat, lng);
        
        if (map) {
          map.setCenter({ lat, lng });
          map.setZoom(15);
        }
        
        setLoading(false);
      },
      (error) => {
        let errorMessage = 'Unable to get your location';
        
        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'Location permission denied. Please enable location access.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'Location information unavailable.';
            break;
          case error.TIMEOUT:
            errorMessage = 'Location request timed out.';
            break;
          default:
            errorMessage = 'An unknown error occurred.';
        }
        
        setError(errorMessage);
        setLoading(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    
    if (!searchQuery.trim()) {
      return;
    }

    const geocoder = new window.google.maps.Geocoder();
    
    geocoder.geocode({ address: searchQuery }, (results, status) => {
      if (status === 'OK' && results[0]) {
        const location = results[0].geometry.location;
        const lat = location.lat();
        const lng = location.lng();
        const address = results[0].formatted_address;

        updateLocation(lat, lng);
        
        if (map) {
          map.setCenter(location);
          map.setZoom(15);
        }

        setSearchQuery(address);
      } else {
        setError('Location not found. Please try a different search.');
      }
    });
  };

  if (error && !map) {
    return (
      <Alert variant="warning">
        <strong>Map Error:</strong> {error}
        <br />
        <small className="mt-2 d-block">
          To use Google Maps, add your API key to the .env file:
          <br />
          <code>REACT_APP_GOOGLE_MAPS_API_KEY=your_api_key_here</code>
          <br />
          And add this script to public/index.html:
          <br />
          <code>
            &lt;script src="https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&libraries=places"&gt;&lt;/script&gt;
          </code>
        </small>
      </Alert>
    );
  }

  return (
    <Card>
      <Card.Body className="p-0">
        {/* Search Bar */}
        {showSearch && (
          <div className="p-3 border-bottom">
            <Form onSubmit={handleSearchSubmit}>
              <InputGroup>
                <InputGroup.Text>
                  <FaSearch />
                </InputGroup.Text>
                <Form.Control
                  ref={searchInputRef}
                  type="text"
                  placeholder="Search for a location or address..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <Button variant="primary" type="submit">
                  Search
                </Button>
              </InputGroup>
            </Form>
          </div>
        )}

        {/* Error Alert */}
        {error && map && (
          <Alert variant="danger" className="m-3 mb-0" dismissible onClose={() => setError('')}>
            {error}
          </Alert>
        )}

        {/* Map Container */}
        <div 
          ref={mapRef}
          style={{ 
            width: '100%', 
            height: height,
            minHeight: '300px'
          }}
        />

        {/* Location Info and Actions */}
        <div className="p-3 bg-light border-top">
          <div className="d-flex justify-content-between align-items-start mb-2">
            <div className="flex-grow-1">
              <small className="text-muted d-block mb-1">
                <FaMapMarkerAlt className="me-1" />
                Selected Location:
              </small>
              <div className="small">
                <strong>Coordinates:</strong> {selectedLocation.lat.toFixed(6)}, {selectedLocation.lng.toFixed(6)}
              </div>
              {selectedLocation.address && (
                <div className="small text-muted">
                  <strong>Address:</strong> {selectedLocation.address}
                </div>
              )}
            </div>
          </div>

          <div className="d-flex gap-2 flex-wrap">
            {showCurrentLocation && (
              <Button 
                variant="primary"
                size="sm"
                onClick={getCurrentLocation}
                disabled={loading}
              >
                {loading ? (
                  <>
                    <Spinner animation="border" size="sm" className="me-1" />
                    Getting Location...
                  </>
                ) : (
                  <>
                    <FaCrosshairs className="me-1" />
                    Use My Location
                  </>
                )}
              </Button>
            )}
            <small className="text-muted align-self-center">
              Click on map or drag marker to select location
            </small>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default LocationPicker;
