import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Badge, Button, Carousel, Alert, Spinner, Modal, Form } from 'react-bootstrap';
import { FaMapMarkerAlt, FaPhone, FaEnvelope, FaEdit, FaTrash, FaUser, FaHeart, FaRegHeart, FaShoppingCart, FaHandHoldingHeart, FaExchangeAlt } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import { getItemById, deleteItem } from '../services/itemService';
import { getItemImages } from '../utils/imageUtils';
import { toggleFavorite } from '../services/favoriteService';
import { createTransaction } from '../services/transactionService';
import { sendMessage } from '../services/messageService';

const ItemDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();

  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isFavorited, setIsFavorited] = useState(false);
  const [favoriteLoading, setFavoriteLoading] = useState(false);
  
  // Transaction modal state
  const [showModal, setShowModal] = useState(false);
  const [transactionMessage, setTransactionMessage] = useState('');
  const [processingTransaction, setProcessingTransaction] = useState(false);

  useEffect(() => {
    fetchItem();
  }, [id]);

  const fetchItem = async () => {
    try {
      setLoading(true);
      const response = await getItemById(id);
      if (response.success) {
        setItem(response.data);
        setIsFavorited(response.data.is_favorited || false);
      } else {
        setError('Item not found');
      }
    } catch (err) {
      setError('Failed to load item details');
    } finally {
      setLoading(false);
    }
  };

  const handleFavoriteToggle = async () => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    // Optimistic UI update
    const previousState = isFavorited;
    setIsFavorited(!isFavorited);
    setFavoriteLoading(true);

    try {
      await toggleFavorite(id, isFavorited);
    } catch (error) {
      console.error('Error toggling favorite:', error);
      // Revert on error
      setIsFavorited(previousState);
      setError('Failed to update favorite status');
    } finally {
      setFavoriteLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await deleteItem(id);
        navigate('/my-items');
      } catch (err) {
        setError('Failed to delete item');
      }
    }
  };

  const handleContactSeller = () => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }
    navigate(`/messages?user=${item.user_id}&item=${item.id}`);
  };

  const handleInitiateTransaction = () => {
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    // Set default message based on listing type
    let defaultMessage = '';
    switch (item.listing_type) {
      case 'sell':
        defaultMessage = `Hi! I'm interested in buying "${item.title}" for Rs. ${parseFloat(item.price).toLocaleString()}. Is it still available?`;
        break;
      case 'donate':
        defaultMessage = `Hi! I would like to request "${item.title}" as a donation. Is it still available?`;
        break;
      case 'exchange':
        defaultMessage = `Hi! I'm interested in exchanging items with you for "${item.title}". Is it still available?`;
        break;
      default:
        defaultMessage = `Hi! I'm interested in "${item.title}". Is it still available?`;
    }
    
    setTransactionMessage(defaultMessage);
    setShowModal(true);
  };

  const handleConfirmTransaction = async () => {
    setProcessingTransaction(true);
    setError('');

    try {
      // Create transaction
      const transactionData = {
        item_id: item.id,
        seller_id: item.user_id,
        buyer_id: user.id,
        amount: item.listing_type === 'sell' ? item.price : 0,
        status: 'pending',
        transaction_type: item.listing_type
      };

      const transactionResponse = await createTransaction(transactionData);

      if (transactionResponse.success) {
        // Send message to seller
        if (transactionMessage.trim()) {
          await sendMessage(
            item.user_id,
            transactionMessage,
            item.id
          );
        }

        // Close modal
        setShowModal(false);

        // Show success message
        alert(`${getActionLabel()} request sent successfully! Redirecting to messages...`);

        // Redirect to messages
        setTimeout(() => {
          navigate(`/messages?user=${item.user_id}&item=${item.id}`);
        }, 1000);
      }
    } catch (err) {
      console.error('Error creating transaction:', err);
      setError(err.response?.data?.message || 'Failed to process request. Please try again.');
    } finally {
      setProcessingTransaction(false);
    }
  };

  const getActionLabel = () => {
    switch (item?.listing_type) {
      case 'sell':
        return 'Buy Now';
      case 'donate':
        return 'Request Donation';
      case 'exchange':
        return 'Request Exchange';
      default:
        return 'Request';
    }
  };

  const getActionIcon = () => {
    switch (item?.listing_type) {
      case 'sell':
        return <FaShoppingCart className="me-2" />;
      case 'donate':
        return <FaHandHoldingHeart className="me-2" />;
      case 'exchange':
        return <FaExchangeAlt className="me-2" />;
      default:
        return null;
    }
  };

  const getCategoryColor = (category) => {
    switch (category) {
      case 'clothes': return 'danger';
      case 'books': return 'info';
      case 'ration': return 'warning';
      default: return 'secondary';
    }
  };

  const getListingTypeBadge = (type) => {
    switch (type) {
      case 'sell': return <Badge bg="success">For Sale</Badge>;
      case 'donate': return <Badge bg="primary">Donate</Badge>;
      case 'exchange': return <Badge bg="warning" text="dark">Exchange</Badge>;
      default: return null;
    }
  };

  if (loading) {
    return (
      <Container className="text-center py-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Loading item details...</p>
      </Container>
    );
  }

  if (error || !item) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'Item not found'}</Alert>
        <Button onClick={() => navigate('/items')}>Back to Items</Button>
      </Container>
    );
  }

  const isOwner = user && user.id === item.user_id;
  const images = getItemImages(item);

  return (
    <Container className="py-4">
      <Row>
        {/* Image Gallery */}
        <Col lg={7}>
          <Card className="mb-4">
            <Carousel>
              {images.map((image, index) => (
                <Carousel.Item key={index}>
                  <img
                    className="d-block w-100"
                    src={image}
                    alt={`${item.title} - ${index + 1}`}
                    style={{ height: '400px', objectFit: 'cover' }}
                  />
                </Carousel.Item>
              ))}
            </Carousel>
          </Card>

          {/* Description */}
          <Card className="mb-4">
            <Card.Body>
              <h5>Description</h5>
              <p className="text-muted">{item.description}</p>
            </Card.Body>
          </Card>

          {/* Location */}
          {item.address && (
            <Card>
              <Card.Body>
                <h5><FaMapMarkerAlt className="me-2" />Location</h5>
                <p className="text-muted mb-0">{item.address}</p>
                {item.latitude && item.longitude && (
                  <small className="text-muted">
                    Coordinates: {item.latitude}, {item.longitude}
                  </small>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>

        {/* Item Details */}
        <Col lg={5}>
          <Card className="mb-4">
            <Card.Body>
              <div className="d-flex justify-content-between align-items-start mb-3">
                <div>
                  <Badge bg={getCategoryColor(item.category)} className="me-2">
                    {item.category}
                  </Badge>
                  {getListingTypeBadge(item.listing_type)}
                </div>
                {item.item_condition && (
                  <Badge bg="secondary">{item.item_condition}</Badge>
                )}
              </div>

              <h2 className="mb-3">{item.title}</h2>

              {item.listing_type === 'sell' && item.price && (
                <h3 className="text-primary mb-4">
                  Rs. {parseFloat(item.price).toLocaleString()}
                </h3>
              )}

              {/* Owner Actions */}
              {isOwner && (
                <div className="d-grid gap-2 mb-4">
                  <Button 
                    variant="warning" 
                    onClick={() => navigate(`/items/${id}/edit`)}
                  >
                    <FaEdit className="me-2" />
                    Edit Item
                  </Button>
                  <Button variant="danger" onClick={handleDelete}>
                    <FaTrash className="me-2" />
                    Delete Item
                  </Button>
                </div>
              )}

              {/* Action Buttons for Non-Owners */}
              {!isOwner && item.status === 'available' && (
                <div className="mb-4">
                  <div className="d-grid gap-2">
                    {/* Primary Action Button */}
                    <Button 
                      variant="success" 
                      size="lg"
                      onClick={handleInitiateTransaction}
                    >
                      {getActionIcon()}
                      {getActionLabel()}
                    </Button>

                    {/* Contact Seller Button */}
                    <Button 
                      variant="primary" 
                      size="lg"
                      onClick={handleContactSeller}
                    >
                      <FaEnvelope className="me-2" />
                      Contact Seller
                    </Button>

                    {/* Favorite Button */}
                    <Button 
                      variant={isFavorited ? 'danger' : 'outline-danger'}
                      size="lg"
                      onClick={handleFavoriteToggle}
                      disabled={favoriteLoading}
                    >
                      {favoriteLoading ? (
                        <Spinner animation="border" size="sm" className="me-2" />
                      ) : isFavorited ? (
                        <FaHeart className="me-2" />
                      ) : (
                        <FaRegHeart className="me-2" />
                      )}
                      {isFavorited ? 'Remove from Favorites' : 'Add to Favorites'}
                    </Button>
                  </div>
                </div>
              )}

              {/* Item Not Available Message */}
              {!isOwner && item.status !== 'available' && (
                <Alert variant="warning" className="mb-4">
                  <strong>This item is no longer available.</strong>
                </Alert>
              )}

              {/* Owner Info */}
              <Card bg="light">
                <Card.Body>
                  <h6 className="mb-3">
                    <FaUser className="me-2" />
                    Seller Information
                  </h6>
                  <p className="mb-2">
                    <strong>Name:</strong> {item.owner_name || 'N/A'}
                  </p>
                  {item.owner_phone && (
                    <p className="mb-2">
                      <FaPhone className="me-2" />
                      {item.owner_phone}
                    </p>
                  )}
                  {item.owner_email && (
                    <p className="mb-0">
                      <FaEnvelope className="me-2" />
                      {item.owner_email}
                    </p>
                  )}
                </Card.Body>
              </Card>
            </Card.Body>
          </Card>

          {/* Item Status */}
          <Card>
            <Card.Body>
              <h6>Item Status</h6>
              <Badge 
                bg={item.status === 'available' ? 'success' : 'secondary'}
                className="fs-6"
              >
                {item.status}
              </Badge>
              <p className="text-muted small mt-2 mb-0">
                Posted on: {new Date(item.created_at).toLocaleDateString()}
              </p>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Transaction Confirmation Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>
            {getActionIcon()}
            Confirm {getActionLabel()}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="mb-3">
            <h6>{item?.title}</h6>
            {item?.listing_type === 'sell' && item?.price && (
              <p className="text-primary mb-0">
                <strong>Price: Rs. {parseFloat(item.price).toLocaleString()}</strong>
              </p>
            )}
          </div>

          <Alert variant="info" className="small">
            <strong>What happens next?</strong>
            <ul className="mb-0 mt-2">
              <li>A transaction request will be created</li>
              <li>Your message will be sent to the seller</li>
              <li>You'll be redirected to the messages page</li>
              <li>The seller will be notified of your request</li>
            </ul>
          </Alert>

          <Form.Group>
            <Form.Label>Message to Seller</Form.Label>
            <Form.Control
              as="textarea"
              rows={4}
              value={transactionMessage}
              onChange={(e) => setTransactionMessage(e.target.value)}
              placeholder="Add a message to the seller..."
            />
            <Form.Text className="text-muted">
              Introduce yourself and express your interest
            </Form.Text>
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button 
            variant="secondary" 
            onClick={() => setShowModal(false)}
            disabled={processingTransaction}
          >
            Cancel
          </Button>
          <Button 
            variant="success" 
            onClick={handleConfirmTransaction}
            disabled={processingTransaction || !transactionMessage.trim()}
          >
            {processingTransaction ? (
              <>
                <Spinner animation="border" size="sm" className="me-2" />
                Processing...
              </>
            ) : (
              <>
                {getActionIcon()}
                Confirm {getActionLabel()}
              </>
            )}
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default ItemDetail;
