import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col, Form, Button, Card, Alert, Image, ProgressBar, Badge } from 'react-bootstrap';
import { FaUpload, FaTimes, FaCheckCircle, FaExclamationCircle } from 'react-icons/fa';
import { createItem } from '../services/itemService';
import { validateFileSize, validateFileType } from '../utils/validation';

const CreateItem = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: 'clothes',
    listing_type: 'sell',
    price: '',
    item_condition: 'good',
    address: '',
    latitude: '',
    longitude: ''
  });

  const [images, setImages] = useState([]);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [imageErrors, setImageErrors] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const { title, description, category, listing_type, price, item_condition, address, latitude, longitude } = formData;

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    
    if (files.length + images.length > 5) {
      setError('Maximum 5 images allowed');
      return;
    }

    setImages([...images, ...files]);

    // Create previews
    files.forEach(file => {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreviews(prev => [...prev, reader.result]);
      };
      reader.readAsDataURL(file);
    });
  };

  const removeImage = (index) => {
    setImages(images.filter((_, i) => i !== index));
    setImagePreviews(imagePreviews.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validation
    if (!title || !description || !category || !listing_type || !item_condition || !address) {
      setError('Please fill in all required fields');
      setLoading(false);
      return;
    }

    if (listing_type === 'sell' && (!price || price <= 0)) {
      setError('Please enter a valid price for selling items');
      setLoading(false);
      return;
    }

    if (!latitude || !longitude) {
      setError('Please provide location coordinates');
      setLoading(false);
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', title);
      formDataToSend.append('description', description);
      formDataToSend.append('category', category);
      formDataToSend.append('listing_type', listing_type);
      formDataToSend.append('item_condition', item_condition);
      formDataToSend.append('address', address);
      formDataToSend.append('latitude', latitude);
      formDataToSend.append('longitude', longitude);

      if (listing_type === 'sell') {
        formDataToSend.append('price', price);
      }

      // Append images
      images.forEach(image => {
        formDataToSend.append('images', image);
      });

      const response = await createItem(formDataToSend);

      if (response.success) {
        navigate(`/items/${response.data.id}`);
      } else {
        setError(response.message || 'Failed to create item');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create item');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="py-4">
      <Row className="justify-content-center">
        <Col lg={8}>
          <Card className="shadow">
            <Card.Body className="p-4">
              <h2 className="mb-4">Create New Item</h2>

              {error && <Alert variant="danger">{error}</Alert>}

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Title *</Form.Label>
                  <Form.Control
                    type="text"
                    name="title"
                    value={title}
                    onChange={handleChange}
                    placeholder="Enter item title"
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Description *</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={4}
                    name="description"
                    value={description}
                    onChange={handleChange}
                    placeholder="Describe your item"
                    required
                  />
                </Form.Group>

                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Category *</Form.Label>
                      <Form.Select name="category" value={category} onChange={handleChange}>
                        <option value="clothes">Clothes</option>
                        <option value="books">Books</option>
                        <option value="ration">Ration</option>
                      </Form.Select>
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Listing Type *</Form.Label>
                      <Form.Select name="listing_type" value={listing_type} onChange={handleChange}>
                        <option value="sell">Sell</option>
                        <option value="donate">Donate</option>
                        <option value="exchange">Exchange</option>
                      </Form.Select>
                    </Form.Group>
                  </Col>
                </Row>

                <Row>
                  {listing_type === 'sell' && (
                    <Col md={6}>
                      <Form.Group className="mb-3">
                        <Form.Label>Price (Rs.) *</Form.Label>
                        <Form.Control
                          type="number"
                          name="price"
                          value={price}
                          onChange={handleChange}
                          placeholder="Enter price"
                          min="0"
                        />
                      </Form.Group>
                    </Col>
                  )}
                  <Col md={listing_type === 'sell' ? 6 : 12}>
                    <Form.Group className="mb-3">
                      <Form.Label>Condition *</Form.Label>
                      <Form.Select name="item_condition" value={item_condition} onChange={handleChange}>
                        <option value="new">New</option>
                        <option value="like_new">Like New</option>
                        <option value="good">Good</option>
                        <option value="fair">Fair</option>
                        <option value="poor">Poor</option>
                      </Form.Select>
                    </Form.Group>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>Address *</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    name="address"
                    value={address}
                    onChange={handleChange}
                    placeholder="Enter full address"
                    required
                  />
                </Form.Group>

                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Latitude *</Form.Label>
                      <Form.Control
                        type="number"
                        step="any"
                        name="latitude"
                        value={latitude}
                        onChange={handleChange}
                        placeholder="e.g., 31.5204"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Longitude *</Form.Label>
                      <Form.Control
                        type="number"
                        step="any"
                        name="longitude"
                        value={longitude}
                        onChange={handleChange}
                        placeholder="e.g., 74.3587"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>Images (Max 5)</Form.Label>
                  <Form.Control
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleImageChange}
                    disabled={images.length >= 5}
                  />
                  <Form.Text className="text-muted">
                    Upload up to 5 images. Supported formats: JPG, PNG, GIF
                  </Form.Text>
                </Form.Group>

                {/* Image Previews */}
                {imagePreviews.length > 0 && (
                  <div className="mb-3">
                    <Form.Label>Image Previews</Form.Label>
                    <Row>
                      {imagePreviews.map((preview, index) => (
                        <Col key={index} xs={6} md={4} lg={3} className="mb-3">
                          <div style={{ position: 'relative' }}>
                            <Image src={preview} thumbnail style={{ height: '150px', objectFit: 'cover', width: '100%' }} />
                            <Button
                              variant="danger"
                              size="sm"
                              className="position-absolute top-0 end-0 m-1"
                              onClick={() => removeImage(index)}
                            >
                              <FaTimes />
                            </Button>
                          </div>
                        </Col>
                      ))}
                    </Row>
                  </div>
                )}

                <div className="d-grid gap-2">
                  <Button variant="primary" type="submit" size="lg" disabled={loading}>
                    {loading ? 'Creating...' : <><FaUpload className="me-2" />Create Item</>}
                  </Button>
                  <Button variant="secondary" onClick={() => navigate(-1)}>
                    Cancel
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default CreateItem;
