import { useState } from 'react';
import { Card, Badge, Button, Spinner } from 'react-bootstrap';
import { FaHeart, FaRegHeart, FaMapMarkerAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { getItemImageUrl } from '../utils/imageUtils';
import { toggleFavorite } from '../services/favoriteService';

const ItemCard = ({ item, onFavoriteToggle }) => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const [isFavorited, setIsFavorited] = useState(item.is_favorited || false);
  const [isLoading, setIsLoading] = useState(false);

  const handleCardClick = () => {
    navigate(`/items/${item.id}`);
  };

  const handleFavoriteClick = async (e) => {
    e.stopPropagation(); // Prevent card click
    
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    // Optimistic UI update
    const previousState = isFavorited;
    setIsFavorited(!isFavorited);
    setIsLoading(true);

    try {
      await toggleFavorite(item.id, isFavorited);
      
      // Notify parent component if callback provided
      if (onFavoriteToggle) {
        onFavoriteToggle(item.id, !isFavorited);
      }
    } catch (error) {
      console.error('Error toggling favorite:', error);
      // Revert on error
      setIsFavorited(previousState);
    } finally {
      setIsLoading(false);
    }
  };

  const getCategoryColor = (category) => {
    switch (category) {
      case 'clothes':
        return 'danger';
      case 'books':
        return 'info';
      case 'ration':
        return 'warning';
      default:
        return 'secondary';
    }
  };

  const getListingTypeBadge = (type) => {
    switch (type) {
      case 'sell':
        return <Badge bg="success">For Sale</Badge>;
      case 'donate':
        return <Badge bg="primary">Donate</Badge>;
      case 'exchange':
        return <Badge bg="warning" text="dark">Exchange</Badge>;
      default:
        return null;
    }
  };

  return (
    <Card 
      className="h-100 shadow-sm hover-shadow" 
      style={{ cursor: 'pointer', transition: 'transform 0.2s' }}
      onClick={handleCardClick}
      onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
      onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
    >
      <div style={{ position: 'relative' }}>
        <Card.Img 
          variant="top" 
          src={getItemImageUrl(item)} 
          style={{ height: '200px', objectFit: 'cover' }}
          alt={item.title}
        />
        
        {/* Favorite Button */}
        <Button
          variant="light"
          size="sm"
          className="position-absolute top-0 end-0 m-2 rounded-circle"
          style={{ width: '40px', height: '40px' }}
          onClick={handleFavoriteClick}
          disabled={isLoading}
          title={isFavorited ? 'Remove from favorites' : 'Add to favorites'}
        >
          {isLoading ? (
            <Spinner animation="border" size="sm" />
          ) : isFavorited ? (
            <FaHeart color="red" size={20} />
          ) : (
            <FaRegHeart size={20} />
          )}
        </Button>

        {/* Category Badge */}
        <Badge 
          bg={getCategoryColor(item.category)} 
          className="position-absolute top-0 start-0 m-2"
        >
          {item.category}
        </Badge>
      </div>

      <Card.Body>
        <div className="d-flex justify-content-between align-items-start mb-2">
          <Card.Title className="mb-0" style={{ fontSize: '1.1rem' }}>
            {item.title}
          </Card.Title>
          {getListingTypeBadge(item.listing_type)}
        </div>

        <Card.Text className="text-muted small mb-2" style={{ 
          overflow: 'hidden', 
          textOverflow: 'ellipsis', 
          display: '-webkit-box', 
          WebkitLineClamp: 2, 
          WebkitBoxOrient: 'vertical' 
        }}>
          {item.description}
        </Card.Text>

        {item.listing_type === 'sell' && item.price && (
          <h5 className="text-primary mb-2">
            Rs. {parseFloat(item.price).toLocaleString()}
          </h5>
        )}

        {item.address && (
          <div className="text-muted small">
            <FaMapMarkerAlt className="me-1" />
            {item.address.length > 30 ? item.address.substring(0, 30) + '...' : item.address}
          </div>
        )}

        {item.item_condition && (
          <Badge bg="secondary" className="mt-2">
            {item.item_condition}
          </Badge>
        )}
      </Card.Body>
    </Card>
  );
};

export default ItemCard;
